<style>
      :host {
      font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
      font-size: 14px;
      color: #333;
      box-sizing: border-box;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
    }

      h1,
      h2,
      h3,
      h4,
      h5,
      h6 {
          margin: 8px 0;
      }

      p {
          margin: 0;
      }

      .content {
          display: flex;
          margin: 82px auto 32px;
          padding: 0 16px;
          max-width: 960px;
          flex-direction: column;
          align-items: center;
      }


      svg#clouds {
          position: fixed;
          bottom: -160px;
          left: -230px;
          z-index: -10;
          width: 1920px;
      }


      .exchange_header {
          width: 100%;
          height: auto;
          background-color: #c4c4c4;
          padding: 15px 10px;
          border-radius: 10px;
      }

      .exchange_header h1 {
          margin-bottom: 14px;
      }

      .currency_list {
          display: flex;
      }

      .ratio_item {
          border: 1px solid black;
          padding: 4px 6px;
          border-radius: 20px;
          margin-left: 10px;
      }
      /* Responsive Styles */
      @media screen and (max-width: 767px) {
          .card-container > *:not(.circle-link),
          .terminal {
              width: 100%;
          }

          .card:not(.highlight-card) {
              height: 16px;
              margin: 8px 0;
          }

          .card.highlight-card span {
              margin-left: 72px;
          }

          svg#rocket-smoke {
              right: 120px;
              transform: rotate(-5deg);
          }
      }

      @media screen and (max-width: 575px) {
          svg#rocket-smoke {
              display: none;
              visibility: hidden;
          }
      }
</style>

