    <div class="exchange_header">
        <h1>Курс валют</h1>
        <div class="currency_list">
            <div class="ratio_item" *ngFor="let key of getObjectValues(CurrentRate)">
                <span class="curr_name">{{ key }}</span> - <span class="curr_ratio">{{ CurrentRate[key] }}</span>
